package circuiti;import java.awt.*;import numeric.Functions;/** * Un componente del circuito */public abstract class Componente  { int label;    public void setLabel(int label)      { this.label=label;      }    public int getLabel()      { return label;      }    public abstract void paint(Graphics g, int x1, int y1, int x2, int y2);    public void paintOrientation(Graphics g, int x1, int y1, int x2, int y2)      { int dx=x2-x1;        int dy=y2-y1;        g.setColor(Color.red);        int dx3= 3*Functions.sign(dx);        int dy3= 3*Functions.sign(dy);        int xa=x1+dx/5+dy/4;        int ya=y1+dy/5-dx/4;        int xb=xa+3*dx3;        int yb=ya+3*dy3;        int xc=xb-dx3+dy3;        int yc=yb-dy3+dx3;        int xd=xb-dx3-dy3;        int yd=yb-dy3-dx3;        g.drawLine(xa, ya, xb, yb);        g.drawLine(xc, yc, xb, yb);        g.drawLine(xd, yd, xb, yb);      }    public int getInfoCount()      { return 3;      }    public String getInfoName(int type)      { String names[]={"Tensione", "Corrente", "Potenza istantanea"};        return names[type];      }    public String getInfoShortName(int type)      { String names[]={"V[V]", "I[A]", "P[W]"};        return names[type];      }    public double getInfo(int type, double V, double I)      { switch(type)          { case 0:              return V;            case 1:              return I;            case 2:              return I*V;            default:              throw new IllegalArgumentException();          }      }  }