package circuiti;import java.awt.*;/** * Un componente attivo del circuito */public abstract class Generatore extends Componente  { protected double Ri=0;    public double getRi()      { return Ri;      }    public void paint(Graphics g, int x1, int y1, int x2, int y2)      { g.setColor(Color.black);        int dx=x2-x1;        int dy=y2-y1;        int xx1=Math.min(x1, x2);        int xx2=Math.max(x1, x2);        int yy1=Math.min(y1, y2);        int yy2=Math.max(y1, y2);        if (dx==0)          { int d=Math.abs(dy);            int q=d/16;            g.drawLine(x1, y1, x1, y1+dy/4);            g.drawLine(x2, y2, x2, y2-dy/4);            g.drawOval(xx1-d/4, yy1+d/4, d/2, d/2);            g.drawLine(x1+dy/4, y1+dy/4-q, x1+dy/4, y1+dy/4+q);            g.drawLine(x1+dy/4-q, y1+dy/4, x1+dy/4+q, y1+dy/4);          }        else          { int d=Math.abs(dx);            int q=d/16;            g.drawLine(x1, y1, x1+dx/4, y1);            g.drawLine(x2, y2, x2-dx/4, y2);            g.drawOval(xx1+d/4, yy1-d/4, d/2, d/2);            g.drawLine(x1+dx/4, y1+dx/4-q, x1+dx/4, y1+dx/4+q);            g.drawLine(x1+dx/4-q, y1+dx/4, x1+dx/4+q, y1+dx/4);          }      }    public abstract double getE(double t);  }