package ottica;import ui.Parameters;import ui.NoParameters;/** * This class contains the settings for the experiment * The following parameters can be used to construct a  * an object through a Parameters instance: * tipo			one of lente, prisma, lastra, superficie * materiale		one of acqua, quarzo, crown, flint, user * rifraz_rosso		1.00 ... 4.00 * rifraz_giallo	1.00 ... 4.00 * rifraz_blu		1.00 ... 4.00 * tipo_lente           one of biconvessa, biconcava,  *                             piano_convessa_sinistra, piano_convessa_destra, *                             piano_concava_sinistra, piano_concava_destra, *                             menisco_sinistra, menisco_destra * lente_raggio_1       0.1 ... 1000 * lente_raggio_2       0.1 ... 1000 * prisma_angolo        one of 0, 45, 90, 135, 180, 225, 270, 315 * lastra_spessore      0.05 ... 1 * tipo_superficie      one of piana, convessa, concava * superficie_riflettente    true/false * superficie_raggio    0.5 ... 1000 *  */public class Settings  { public static final int LENTE=0,                            PRISMA=1,  			    LASTRA=2,  			    SUPERFICIE=3;    public int tipo;    public static final int ACQUA=0,                            QUARZO=1,                            CROWN=2,                            FLINT=3,                            USER=4;    public int materiale;    public static final double tabella_rifrazione[][]=                                {{1.331, 1.332, 1.333},                                 {1.443, 1.444, 1.445},                                 {1.55, 1.55, 1.55},                                 {1.66, 1.66, 1.66}};    public double rifraz_rosso=1.0,                  rifraz_giallo=1.0,                  rifraz_blu=1.0;    public static final int BICONVESSA=0,                            BICONCAVA=1,                            PIANO_CONVESSA_SINISTRA=2,                            PIANO_CONVESSA_DESTRA=3,                            PIANO_CONCAVA_SINISTRA=4,                            PIANO_CONCAVA_DESTRA=5,                            MENISCO_SINISTRA=6,                            MENISCO_DESTRA=7;    public int tipo_lente;    public double lente_raggio_1,                  lente_raggio_2;    public static final int PRISMA_0=0,                            PRISMA_45=1,                            PRISMA_90=2,                            PRISMA_135=3,                            PRISMA_180=4,                            PRISMA_225=5,                            PRISMA_270=6,                            PRISMA_315=7;    public int prisma_angolo;    public double lastra_spessore;    public static final int PIANA=0,                            CONVESSA=1,                            CONCAVA=2;    public int tipo_superficie;    public double superficie_raggio;    public boolean superficie_riflettente;    public static double INFTY=Double.POSITIVE_INFINITY;    /**     * Default constructor     */    public Settings()      { this(new NoParameters());      }    /**     * Use the Paremeters object for the value of the fields     */    public Settings(Parameters parm)      { String lista_tipi[]={"lente", "prisma", "lastra", "superficie"};        tipo=parm.getFromList("tipo", lista_tipi, SUPERFICIE);        String lista_materiali[]={"acqua", "quarzo", "crown", "flint", "user"};        materiale=parm.getFromList("materiale", lista_materiali, USER);        if (materiale!=USER)          { rifraz_rosso=tabella_rifrazione[materiale][0];            rifraz_giallo=tabella_rifrazione[materiale][1];            rifraz_blu=tabella_rifrazione[materiale][2];          }        else          { rifraz_rosso=parm.getDouble("rifraz_rosso", 1.33, 1.0, 4.0);            rifraz_giallo=parm.getDouble("rifraz_giallo", 1.33, 1.0, 4.0);            rifraz_blu=parm.getDouble("rifraz_blu", 1.33, 1.0, 4.0);          }        String lista_tipi_lente[]=                  {"biconvessa", "biconcava",                   "piano_convessa_sinistra", "piano_convessa_destra",                   "piano_concava_sinistra", "piano_concava_destra",                   "menisco_sinistra", "menisco_destra"};        tipo_lente=parm.getFromList("tipo_lente", lista_tipi_lente, BICONVESSA);        lente_raggio_1=parm.getDouble("lente_raggio_1", 1.0, 0.1, 1000.0);        lente_raggio_2=parm.getDouble("lente_raggio_2", 0.8, 0.1, 1000.0);        prisma_angolo=parm.getInt("prisma_angolo", 0, 0, 360)/45;        lastra_spessore=parm.getDouble("lastra_spessore", 0.5, 0.05, 1.0);        String lista_tipi_superficie[]={"piana", "convessa", "concava"};        tipo_superficie=parm.getFromList("tipo_superficie",                                          lista_tipi_superficie, PIANA);        superficie_raggio=parm.getDouble("superficie_raggio", 1.5, 0.5, 1000.0);                superficie_riflettente=               parm.getBoolean("superficie_riflettente", false);      }  }