package ui;import java.awt.*;/** * This class allows to change the cursor for  * AWT 1.0 components that are not frames */public class CursorChanger  { Frame frame;    Component com;    public CursorChanger(Component com)      { this.com=com;        frame=null;      }    void seekFrame()      { Component c=com;        while (c!=null && !(c instanceof Frame))          { c=c.getParent();          }        frame=(Frame)c;              }    public int getCursorType()      { if (frame==null)          seekFrame();        if (frame!=null)          return frame.getCursorType();        else          return Frame.DEFAULT_CURSOR;      }    public void setCursor()      { setCursor(Frame.DEFAULT_CURSOR);      }    public void setCursor(int type)      { if (frame==null)          seekFrame();        if (frame!=null)          { frame.setCursor(type);          }      }  }