package ui;import java.awt.*;/** * A canvas which is painted by an external object * @see ui.Painter */public class PaintableCanvas extends Canvas  { Painter painter=null;    public void setPainter(Painter painter)      { this.painter=painter;      }    public void paint(Graphics g)      { super.paint(g);        if (painter!=null)          { painter.paint(this, g);          }      }  }