package ui;import java.lang.System;	  //import com.sun.java.swing.*;                         import java.awt.*;public class TrackedFrame extends Dialog {        public synchronized void setIconImage(Image image) {        WindowsTracker.firstwin.setIconImage(image);            }        public TrackedFrame() {                 super (WindowsTracker.firstwin); // delete for frame        WindowsTracker.add(this);        setModal(false); // delete for frame        setResizable(false); // delete for frame                    }    public TrackedFrame (String  title) {        super (WindowsTracker.firstwin); // delete for frame        WindowsTracker.add(this);        setTitle(title);        setModal(false); // delete for frame        setResizable(false); // delete for frame            }        public void dispose() {        WindowsTracker.delete(this);        super.dispose();    }	    public void centerOnScreen() {		Dimension s=getToolkit().getScreenSize();		if (WindowsTracker.fullscreen) {			setSize(s.width,s.height);			System.out.print ("Screen size: ");			System.out.println(s);						}				else			setSize(512,460);		Dimension d=size();		move( (s.width-d.width)/2, (s.height-d.height)/2 );    }}