package ui.animlabel;import java.awt.*;/** * Adds a shadow behind each character */public class Shadow implements Animation  { Color color=Color.gray;    int dx=3, dy=3;    public void setColor(Color color)      { this.color=color;      }    public void setOffset(int dx, int dy)      { this.dx=dx;        this.dy=dy;      }    public void perform(Graphics g, int frame, int idx, Token tok)      { g.setFont(tok.font);        g.setColor(color);        g.drawString(""+tok.ch, tok.pos.x+dx, tok.pos.y+dy);      }  }